namespace Mageplaza\SecurityPro\Ui\DataProvider;

use Magento\Framework\Model\ResourceModel\Db\Collection\AbstractCollection;
use Magento\Ui\DataProvider\AbstractDataProvider;
use Mageplaza\SecurityPro\Model\ResourceModel\ActionLogBackup\Collection;
use Mageplaza\SecurityPro\Model\ResourceModel\ActionLogBackup\CollectionFactory;

/**
 * Class ActionLogBackup
 * @package Mageplaza\SecurityPro\Ui\DataProvider
 */
class ActionLogBackup extends AbstractDataProvider
{
    /**
     * @var Collection
     */
    protected $collection;

    /**
     * ActionLogBackup constructor.
     *
     * @param string $name
     * @param string $primaryFieldName
     * @param string $requestFieldName
     * @param CollectionFactory $collectionFactory
     * @param array $meta
     * @param array $data
     */
    public function __construct(
        $name,
        $primaryFieldName,
        $requestFieldName,
        CollectionFactory $collectionFactory,
        array $meta = [],
        array $data = []
    ) {
        parent::__construct($name, $primaryFieldName, $requestFieldName, $meta, $data);

        $this->collection = $collectionFactory->create();
    }

    /**
     * @return AbstractCollection|Collection
     */
    public function getCollection()
    {
        return $this->collection;
    }

    /**
     * Get data
     *
     * @return array
     */
    public function getData()
    {
        if (!$this->getCollection()->isLoaded()) {
            $this->getCollection()->load();
        }

        return $this->getCollection()->toArray();
    }
}
